in" => "metais.finance.gov.sk", "user_id" => 7, "internal" => false, "attachment" => false, "reflection" => false, "fancy_title" => nil }, { "url" => "https://www.minv.sk/?schvalene-rz&subor=245880", "title" => nil, "clicks" => 0, "domain" => "www.minv.sk", "user_id" => 7, "internal" => false, "attachment" => false, "reflection" => false, "fancy_title" => nil }, { "url" => "https://www.opii.gov.sk/opiiapp.php/Vyzvania/show?id=369", "title" => "Operačný program Integrovaná infraštruktúra :: Vyhľadávanie vyzvaní", "clicks" => 0, "domain" => "www.opii.gov.sk", "user_id" => 7, "internal" => false, "attachment" => false, "reflection" => false, "fancy_title" => nil }], "created_by" => { "id" => 7, "username" => "Lubor", "avatar_template" => "/letter_avatar_proxy/v2/letter/l/96bed5/{size}.png" }, "last_poster" => { "id" => 226, "username" => "vojtob", "avatar_template" => "/letter_avatar_proxy/v2/letter/v/3da27b/{size}.png" }, "participants" => [{ "id" => 7, "username" => "Lubor", "post_count" => 1, "avatar_template" => "/letter_avatar_proxy/v2/letter/l/96bed5/{size}.png", "primary_group_name" => "core-team", "primary_group_flair_url" => "fa-asterisk ", "primary_group_flair_color" => "fff", "primary_group_flair_bg_color" => "fd0" }, { "id" => 226, "username" => "vojtob", "post_count" => 1, "avatar_template" => "/letter_avatar_proxy/v2/letter/v/3da27b/{size}.png", "primary_group_name" => nil, "primary_group_flair_url" => nil, "primary_group_flair_color" => nil, "primary_group_flair_bg_color" => nil }], "can_flag_topic" => false, "notification_level" => 1 }, "user_id" => 7, "visible" => true, "archived" => false, "can_vote" => false, "unpinned" => nil, "archetype" => "regular", "draft_key" => "topic_4228", "pinned_at" => nil, "bookmarked" => nil, "chunk_size" => 20, "created_at" => "2017-09-30T15:40:30.319Z", "deleted_at" => nil, "deleted_by" => nil, "like_count" => 2, "user_voted" => false, "vote_count" => nil, "word_count" => 1187, "category_id" => 43, "fancy_title" => "Red Flags: IS Obchodného registra", "has_summary" => false, "post_stream" => { "posts" => [{ "id" => 20183, "name" => "Ľubor Illek", "read" => true, "wiki" => true, "admin" => true, "reads" => 33, "score" => 79.65, "staff" => true, "yours" => false, "cooked" => "<p><strong>Názov:</strong> Informačný systém Obchodného registra (IS OR)</p><p><strong>Garant:</strong> Ministerstvo spravodlivosti SR</p>\n<p><strong>Stručný opis:</strong> Implementovanť nový IS pre správu Obchodného registra a súvisiace opatrenia na zafektívnenie jeho procesov.</p>\n<p><strong>Náklady na projekt:</strong> 13 250 000 EUR (s DPH, podľa vyzvania na národný projekt)</p>\n<p><strong>Aktuálny stav projektu:</strong> Výzva na národný projekt v OPII</p><p><strong>Zhrnutie hodnotenia Red Flags:</strong> Najvýraznejším nedostatkom projektu EDUNET je nedostatočná príprava projektu, chýbajúce zhodnotenie alternatív a ekonomické zhodnotenie rôznych modelov nákupu a budovania školskej siete. Projekt je nastavený tak, že na trhu s viac ako 300 dodávateľmi sa obstarávania zúčastnili len štyria. Ministerstvo taktiež odmieta odbornú diskusiu k projektu, čo výrazne zvyšuje obavu o jeho kvalitu.</p>
<p><strong>Stanovisko Slovensko.Digital:</strong> Odporúčame projekt pozastaviť a dôkladne zanalyzovať rôzne alternatívy budovania siete aj s ohľadom na podmienky na telekomunikačnom trhu.</p>\n<h1>\n<img src=\"//platforma.slovensko.digital/images/emoji/twitter/triangular_flag_on_post.png?v=5\" title=\":triangular_flag_on_post:\" class=\"emoji\" alt=\":triangular_flag_on_post:\">  <a href=\"https://platforma.slovensko.digital/t/o-kategorii-red-flags/4034\">HODNOTENIE RED FLAGS</a>\n</h1>\n<h2>I. Prípravná fáza</h2>\n<hr>\n<h3>Reforma VS <img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/b/bdd2e11053ea53c9b119412c78ec0994497635b3.png?v=5\" title=\":grey_star:\" class=\"emoji emoji-custom\" alt=\":grey_star:\"><img src=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/b/bdd2e11053ea53c9b119412c78ec0994497635b3.png?v=5\" title=\":grey_star:\" class=\"emoji emoji-custom\" alt=\":grey_star:\">\n</h3>\n<p>Projekt deklaruje optimalizáciu procesov priamo súvisiacich s Obchodným registrom.<br>\nKonkrétne bola identifikovaná zmena v rozsahu subjektov ktoré môžu zapísať spoločnosť do OR.</p>\n<p>Projekt nerieši komplexne životné situácie súvisiace s podnikaním.<br>\nNapr. celkový priemerný čas na založenie obchodnej spoločnosti je 45 dní (viď. RZ kap. 1). Tento projekt nerieši situáciu komplexne, ale iba časť týkajúcu sa zapísania do OR - z 2 dní na “obratom”. Aj v prípade uskutočnenia cieľov projektu sa výsledný celkový čas teda zlepší najviac o 5%.</p>\n<hr>\n<h3>Merateľné ciele (KPI)  <img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/b/bdd2e11053ea53c9b119412c78ec0994497635b3.png?v=5\" title=\":grey_star:\" class=\"emoji emoji-custom\" alt=\":grey_star:\">\n</h3>\n<p>Podľa Reformného zámeru:</p>\n<p><div class=\"lightbox-wrapper\"><a href=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/9/9d20430e2999f9b8b4213e379807a55c882df401.PNG\" class=\"lightbox\" title=\"Capture.PNG\"><img src=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/optimized/2X/9/9d20430e2999f9b8b4213e379807a55c882df401_1_690x311.PNG\" alt=\"Capture\" width=\"690\" height=\"311\"><div class=\"meta\">\n<span class=\"filename\">Capture.PNG</span><span class=\"informations\">757x342 47.5 KB</span><span class=\"expand\"></span>\n</div></a></div><br>\n<div class=\"lightbox-wrapper\"><a href=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/2/2f03dd98d3c8fdd5ad13f26a7a1d56811b9dc2f6.PNG\" class=\"lightbox\" title=\"Capture.PNG\"><img src=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/optimized/2X/2/2f03dd98d3c8fdd5ad13f26a7a1d56811b9dc2f6_1_690x393.PNG\" alt=\"Capture\" width=\"690\" height=\"393\"><div class=\"meta\">\n<span class=\"filename\">Capture.PNG</span><span class=\"informations\">717x409 46.9 KB</span><span class=\"expand\"></span>\n</div></a></div></p>\n<p>“Termínom “obratom” sa v podmienkach Obchodného registra rozumie do konca dňa, obchodný register bude aktualizovaný na dennej báze.” (podľa Štúdie uskutočniteľnosti)</p>\n<p>Štúdia uskutočniteľnosti:</p>\n<ul>\n<li>Dôležitým výstupovým ukazovateľom projektu je zabezpečenie 100% kvality a integrity dát v obchodnom registry. (kap. 1.1)</li>\n<li>Dáta z obchodného registra budú použiteľné na právne účely on-line, čím sa eliminuje potreba výpisov z obchodného registra. (kap 1.1)</li>\n</ul>\n<p>Nie sú známe merateľné ciele súvisiace s optimalizáciou vnútorného prostredia verejnej správy súvisiacej s agendou OR, čo je jeden z deklarovaných zámerov projektu.</p>\n<hr>\n<h3>Postup dosiahnutia cieľov  <img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/b/bdd2e11053ea53c9b119412c78ec0994497635b3.png?v=5\" title=\":grey_star:\" class=\"emoji emoji-custom\" alt=\":grey_star:\">\n</h3>\n<p>Projekt je rozdelený na dve etapy, prvá etapa sú funkcie pre ktoré nie je vyžadovaná legislatívna zmena (podľa štúdie uskutočniteľnosti). Štúdia uskutočniteľnosti obsahuje detailný harmonogram činností.<br>\nOd iných subjektov je pri realizácii projektu závislá iba integrácia na referenčné údaje.</p>\n<p>Nie je známe, akým spôsobom bude realizované dosiahnutie cieľa “100% kvality dát v registri”, ktoré bude náročné aj vzhľadom na historické skúsenosti v tejto oblasti.</p>\n<hr>\n<h3>Súlad s KRIS (nie je zatiaľ vyhodnotený)</h3>\n<p>Súlad s KRIS či už ÚPVII, alebo OVM, ktoré majú byť do projektu zapojené, sme zatiaľ nevyhodnotili.<br>\nVšetky KRIS majú do konca roka 2017 prejsť podstatnou aktualizáciou, doplnený by mal byť cieľový stav ISVS.</p>\n<hr>\n<h3>Biznis prínos <img src=\"//platforma.slovensko.digital/images/emoji/twitter/triangular_flag_on_post.png?v=5\" title=\":triangular_flag_on_post:\" class=\"emoji\" alt=\":triangular_flag_on_post:\">\n</h3>\n<p>Projekt má podstatne zjednodušiť internú administratívu súvisiacu s ORSR.<br>\nImplementácia princípu “Digital by default” v rámci interného spracovania agendy.<br>\nV súčasnosti je podstatná časť agiend OR už podporovaná špecializovaným IS, ide teda skôr o postupné zlepšenie v tejto oblasti.</p>\n<hr>\n<h3>Príspevok v informatizácii <img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\">\n</h3>\n<p>Súčasťou projektu je riešenie kvality údajov (cieľ - 100% správnych údajov v registri).<br>\nOd začiatku sú podporované princípy OpenData, OpenAPI, zverejnenie právne záväzných údajov.<br>\nProjekt počíta s integráciou na všetky registre obsahujúce všetky údaje potrebné na jednotlivé procesy OR, čím sa zabezpečí princíp 1x a dosť voči OR.<br>\nÚdaje OR majú byť referenčné a jednoducho použiteľné pre ostatné OVM. V tejto oblasti nie je jasné rozdelenie kompetencií medzi ORSR a RPO.</p>\n<p>Podľa štúdie bude v relevantných moduloch zvážený agilný prístup vývoja a možnosť obstarať časti ako službu (a nie ako dielo).</p>\n<p>V ďalších etapách projektu treba overiť, že uvažované je používateľsky prívetivé grafické rozhranie, nie len formulárový systém.</p>\n<hr>\n<h3>Štúdia uskutočniteľnosti  <img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\">\n</h3>\n<p>Štúdia uskutočniteľnosti bola vypracovaná, bolo umožnené jej pripomienkovanie, vzhľadom na (malý) rozsah projektu obsahuje dostatočnú mieru detailov, konkrétnych cieľov a postupov.<br>\nUvedený prehľad v súčasnosti prevádzkovaného IS a súvisiacich nákladov.<br>\nNavrhnutý nový IS je prehľadne členený na moduly.</p>\n<hr>\n<h3>Alternatívy <img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/b/bdd2e11053ea53c9b119412c78ec0994497635b3.png?v=5\" title=\":grey_star:\" class=\"emoji emoji-custom\" alt=\":grey_star:\"><img src=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/b/bdd2e11053ea53c9b119412c78ec0994497635b3.png?v=5\" title=\":grey_star:\" class=\"emoji emoji-custom\" alt=\":grey_star:\">\n</h3>\n<p>V Štúdii uskutočniteľnosti je podrobne analyzovaná možnosť rozvoja súčasného IS a vybudovanie nového IS a dopady rozšírenia okruhu registrátorov obchodných spoločností.<br>\nNie sú analyzované varianty v závislosti od spôsobu realizácie jednotlivých modulov (napr. riešenie integrácií prostredníctvom MÚK vs. integrácie priamo G2G, obstaranie niektorých komponentov v režime SaaS).<br>\nNie sú analyzované náklady a prínosy pri realizácie “nepovinných” súčastí, napr. publikovanie profilov obchodných firiem, resp. porovnanie s už existujúcimi riešeniami (napr. v komerčnej sfére).</p>\n<hr>\n<h3>Kalkulácia efektívnosti <img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/b/bdd2e11053ea53c9b119412c78ec0994497635b3.png?v=5\" title=\":grey_star:\" class=\"emoji emoji-custom\" alt=\":grey_star:\"><img src=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/b/bdd2e11053ea53c9b119412c78ec0994497635b3.png?v=5\" title=\":grey_star:\" class=\"emoji emoji-custom\" alt=\":grey_star:\"><img src=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/b/bdd2e11053ea53c9b119412c78ec0994497635b3.png?v=5\" title=\":grey_star:\" class=\"emoji emoji-custom\" alt=\":grey_star:\">\n</h3>\n<p>CBA uvádzanú v štúdii sme zatiaľ podrobne neanalyzovali. Vyzvanie na národný projekt obsahuje viaceré verzie CBA, s vzájomne nekonzistentnými hodnotami položiek.<br>\nNiektoré položky projektu, napr. náklady na OpenData a OpenAPI - 400.000€, kalkulované náklady potrebné na integrácie G2G a BRIS - 2,2M€ sa javia ako neobvykle vysoké.<br>\nNie je známe, akým spôsobom boli odhady nákladov na jednotlivé položky stanovené.</p>\n<hr>\n<h3>Participácia na príprave projektu <img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\"><img src=\"//platforma.slovensko.digital/images/emoji/twitter/star.png?v=5\" title=\":star:\" class=\"emoji\" alt=\":star:\">\n</h3>\n<p>K reformnému zámeru aj štúdii uskutočniteľnosti bolo možné poslať pripomienky.<br>\nMinisterstvo prizvalo vybrané subjekty (vrátane S.D), mohli sa vyjadriť a dať pripomienky k štúdii vo fáze draftu.<br>\nUskutočnilo sa verejné prerokovanie štúdie, za účasti ministerky.</p>\n<p>Diskusie k projektu na platforme:</p>\n<ul>\n<li><a href=\"https://platforma.slovensko.digital/t/obchodny-register-sr/3358\">o štúdii uskutočniteľnosti</a></li>\n<li><a href=\"https://platforma.slovensko.digital/t/vseobecne-dostupna-el-zbierka-listin-obchodneho-registra/3230\">o novele zákona ktorou sa prepájajú EÚ registre / BRIS</a></li>\n<li><a href=\"https://platforma.slovensko.digital/t/implementacia-zmien-uprav-informacneho-systemu-obchodneho-registra-corwin-suvisiaca-so-zavedenim-noveho-typu-spolocnosti-s-nazvom-jednoducha-spolocnost-na-akcie-etapa-1-analyza-etapa-2-implementacia/3192\">projekt na zmeny súčasného IS Corwin</a></li>\n</ul>\n<hr>\n<h1>\n<img src=\"//platforma.slovensko.digital/images/emoji/twitter/file_folder.png?v=5\" title=\":file_folder:\" class=\"emoji\" alt=\":file_folder:\"> Dokumenty</h1>\n<ul>\n<li>\n<p>Reformný zámer: <a class=\"attachment\" href=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/1/15854e70f13904751d2c879945bafaf435440b32.pdf\">12.RZ-MSSR-Zlepsenie podnikatelskeho prostredia a odbremenenie sudneho systemu.pdf</a> (986.3 KB)</p>\n</li>\n<li>\n<p>pripomienky k Reformnému zámeru a ich zapracovanie: potrebné získať od MV SR /EVS</p>\n</li>\n<li>\n<p>Štúdia uskutočniteľnosti:</p>\n<ul>\n<li><a href=\"https://metais.finance.gov.sk/studia/detail/67cab118-1a08-e9dc-a136-a2f3a51811dd?tab=basicForm\">online</a></li>\n<li>(vybrané z príloh vyzvania) <a class=\"attachment\" href=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/e/e351d6d3c5cc79222275f78d0774aa96483f1c5f.pdf\">SU_IS OR SR_su47.pdf</a> (1.7 MB)</li>\n</ul>\n</li>\n<li>\n<p>Vyzvanie na národný projekt:</p>\n<ul>\n<li><a href=\"https://www.opii.gov.sk/opiiapp.php/Vyzvania/show?id=369\">online</a></li>\n<li>\n<a class=\"attachment\" href=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/a/a34b54a00960e3af7fa92b736115b9e4620e5e83.PDF\">vyzvanie č. OPII-201773-NP Národného projektu Informačný systém Obchodného registra SR (2).PDF</a> (515.5 KB)</li>\n<li>\n<a class=\"attachment\" href=\"//platforma-slovensko-digital-uploads.s3-eu-central-1.amazonaws.com/original/2X/5/52f2f45c3668014320214a9926ce30d98978536e.zip\">Prilohy_vyzvania_ISOR (1).zip</a> (3.0 MB)</li>\n</ul>\n</li>\n<li>\n<p>pripomienky k Štúdii uskutočniteľnosti a ich zapracovanie: potrebné získať od MSSR alebo ÚPVII</p>\n</li>\n<li>\n<p>Procesno organizačný audit rezortu spravodlivosti (zameraná na registráciu obchodných spoločností): potrebné získať od MSSR - podľa RZ bol vypracovaný v prvom kvartáli 2017</p>\n</li>\n</ul>\n<hr>\n<h1>\n<img src=\"//platforma.slovensko.digital/images/emoji/twitter/clock2.png?v=5\" title=\":clock2:\" class=\"emoji\" alt=\":clock2:\"> Aktivity</h1>\n<p>V tomto projekte už prebehli nasledovné dôležité aktivity / míľniky:</p>\n<ul>\n<li>14.7.2016 Reformný zámer <a href=\"https://www.minv.sk/?schvalene-rz&amp;subor=245880\">schválený</a> HK OP EVS</li>\n<li>2.6.2017 Štúdia uskutočniteľnosti schválená RV OPII PO 7</li>\n<li>18.7.2017 v OPII PO 7 <a href=\"http://www.informatizacia.sk/aktuality-tlacova-sprava-k-vyzvaniu-na-narodny-projekt-informacny-system-obchodneho-registra/25472c\">zverejnená výzva</a> na Národný projekt IS Obchodného registra</li>\n</ul>", "hidden" => false, "user_id" => 7, "version" => 2, "avg_time" => 65, "can_edit" => false, "can_wiki" => false, "topic_id" => 4228, "username" => "Lubor", "moderator" => true, "post_type" => 1, "can_delete" => false, "created_at" => "2017-09-30T15:40:30.482Z", "deleted_at" => nil, "topic_slug" => "red-flags-is-obchodneho-registra", "updated_at" => "2017-09-30T16:07:15.931Z", "user_title" => "Tím Slovensko.Digital", "can_recover" => false, "edit_reason" => nil, "link_counts" => [{ "url" => "http://www.informatizacia.sk/aktuality-tlacova-sprava-k-vyzvaniu-na-narodny-projekt-informacny-system-obchodneho-registra/25472c", "title" => "INFORMATIZÁCIA - Aktuálne", "clicks" => 3, "internal" => false, "reflection" => false }, { "url" => "http://platforma.slovensko.digital/t/o-kategorii-red-flags/4034", "title" => "O projekte Red Flags", "clicks" => 3, "internal" => true, "reflection" => false }, { "url" => "//platforma-slovensko-digital-uploads.s3-